var getMobileOS={init:function(e){var t,n;if(e){t=e}else{t=navigator.userAgent}if(t.match(/iPad/i)){getMobileOS.device="iPad";getMobileOS.os="ios";n=t.indexOf("OS ")}else if(t.match(/iPod/i)){getMobileOS.device="iPod";getMobileOS.os="ios";n=t.indexOf("OS ")}else if(t.match(/iPhone/i)){getMobileOS.device="iPhone";getMobileOS.os="ios";n=t.indexOf("OS ")}else if(t.match(/Android/i)){getMobileOS.device="Android";getMobileOS.os="android";n=t.indexOf("Android ")}else{getMobileOS.device="unknown";getMobileOS.os=false}if(getMobileOS.os==="ios"&&n>-1){getMobileOS.ver=t.substr(n+3,3).replace("_",".");return true}else if(getMobileOS.os==="android"){getMobileOS.ver=t.substr(n+8,3);return true}else{getMobileOS.ver=false;return false}}}
